import React from 'react';
import './LoginForm.css';
import axios from 'axios';
import { withRouter } from "react-router-dom";
import { Cookies } from "react-cookie";

class LoginForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      fields: {},
      errors: {}
    };
  };
  handleValidation() {
    let fields = this.state.fields;
    let errors = {};
    let formIsValid = true;
    //Email
    if (!fields["email"]) {
      formIsValid = false;
      errors["email"] = "Không được để trống";
    }
    if (typeof fields["email"] !== "undefined") {

      var pattern = new RegExp(/^(("[\w-\s]+")|([\w-]+(?:\.[\w-]+)*)|("[\w-\s]+")([\w-]+(?:\.[\w-]+)*))(@((?:[\w-]+\.)*\w[\w-]{0,66})\.([a-z]{2,6}(?:\.[a-z]{2})?)$)|(@\[?((25[0-5]\.|2[0-4][0-9]\.|1[0-9]{2}\.|[0-9]{1,2}\.))((25[0-5]|2[0-4][0-9]|1[0-9]{2}|[0-9]{1,2})\.){2}(25[0-5]|2[0-4][0-9]|1[0-9]{2}|[0-9]{1,2})\]?$)/i);
      if (!pattern.test(fields["email"])) {
        formIsValid = false;
        errors["email"] = "Email không hợp lệ";
      }
    }
    //Pass
    if (!fields["password"]) {
      formIsValid = false;
      errors["password"] = "Không được để trống ";
    }
    this.setState({ errors: errors });
    return formIsValid;
  }
  contactSubmit(e) {

    let cookie = new Cookies();


    e.preventDefault();

    let user = {
      user_name: this.state.fields.email,
      pass: this.state.fields.password
    }

    console.log("user", user);
    // if (this.handleValidation()) {
    axios.post("http://53e30b7675de.ngrok.io/api/auth/login",
      user
    ).then(
      (res) => {
        console.log("res", res);
        let access_token = res.data.Object.access_token;


        cookie.set('access_token',access_token); 

        console.log( cookie.get('access_token')); 

        this.props.history.push('/home');
      }
    ).catch(
      (err) => {
        console.log("err", err);
      }
    );
    //   alert("Đăng nhập thành công");
    // } else {
    //   alert("Đăng nhập thất bại")
    // }

  }

  handleChange(field, e) {
    let fields = this.state.fields;
    fields[field] = e.target.value;
    this.setState({ fields });
  }

  render() {
    return (
      <main>
        <div className="container">
          <div className="login-form">
            <form onSubmit={this.contactSubmit.bind(this)}>
              <h1>Đăng Nhập Người Dùng</h1>

              <div className="input-box">
                <label for="">Email</label>
                <p>
                  <input ref="email" name="email" type="text" placeholder="Nhập tên người dùng"
                    onChange={this.handleChange.bind(this, "email")} value={this.state.fields["email"]} />
                  <span style={{ color: "red" }}>{this.state.errors["email"]}</span>
                </p>
              </div>
              <div className="input-box">
                <label for="">Password</label>
                <input refs="password" name="password" type="password" placeholder="Nhập mật khẩu"
                  onChange={this.handleChange.bind(this, "password")} value={this.state.fields["password"]} />
                <span style={{ color: "red" }}>{this.state.errors["password"]}</span>
              </div>

              <div className="btn-box">
                <button type="submit" value="Submit">
                  Đăng nhập
                   </button>
              </div>
                  Or <a href="/register">register now!</a>
            </form>
          </div>
        </div>
      </main>
    )
  }
}

export default withRouter(LoginForm);
