import React from 'react';
import './LoginForm.css';
import axios from 'axios';

class LoginForm extends React.Component {
  constructor(props){
    super(props);
    this.state={
      fields:{},
      errors:{}
    };
  };
  handleValidation(){
          let fields=this.state.fields;
          let errors={};
          let formIsValid= true;
          //Name
          if (!fields["name"]) {
            formIsValid = false;
            errors["name"] = "Không được để trống";
          }

          if (typeof fields["name"] !== "undefined") {
            if (!fields["name"].match(/^[a-zA-Z0-9]+$/)) {
              formIsValid = false;
              errors["name"] = "Không được sử dụng ký tự";
            }
          }
        //Pass
        if(!fields["password"]){
          formIsValid =false;
          errors["password"]="Không được để trống ";
        }
        this.setState({errors:errors});
        return formIsValid;
      }
      handleLogin() {
        const user = {
          name: this.state.name,
          pass: this.state.pass
        }
        axios.post(`http://857acd8fe305.ngrok.io/api/auth/login`, { user })
          .then(res => {
            console.log(res);
            console.log(res.data);
            if (res.data.err === 0) {
              this.setState({
                text: "welcome"
              })
            }
            else{
              this.setState({
                text: "Đăng nhập thất bại"
              })
            }
          })
      }
      // contactSubmit(e){
      //   e.preventDefault();

      //   if(this.handleValidation()){
      //     alert("Đăng nhập thành công");
      //   }else{
      //     alert("Đăng nhập thất bại")
      //   }

      // }

      handleChange(field, e){         
        let fields = this.state.fields;
        fields[field] = e.target.value;        
        this.setState({fields});
      }

    render() {
        return (
            <main>
            <div className="container">
              <div className="login-form">
                <form onSubmit= {this.handleLogin.bind(this)}>
                  <h1>Đăng Nhập Người Dùng</h1>

                  <div className="input-box">
                    <label for="">Name</label>
                    <p>
                    <input ref="name" name="name" type="text" placeholder="Nhập tên người dùng" 
                     onChange={this.handleChange.bind(this, "name")} value={this.state.fields["name"]}/>
                     <span style={{color: "red"}}>{this.state.errors["name"]}</span>
                    </p>
                  </div>
                  <div className="input-box">
                    <label for="">Password</label>
                    <input refs="password" name="password" type="password" placeholder="Nhập mật khẩu" 
                    onChange={this.handleChange.bind(this, "password")} value={this.state.fields["password"]}/>
                    <span style={{color: "red"}}>{this.state.errors["password"]}</span>
                  </div>

                  <div className="btn-box">
                    <button type="submit"value="Submit">
                      Đăng nhập
                   </button>
                  </div>
                  Or <a href="/register">register now!</a>
                </form>
              </div>
            </div>
          </main>
        )
    }
}

export default LoginForm;
