import React from 'react';
import posts from '../../services/post/Postlist';
import PostItem from '../Post/PostItem';
import { Pagination, PaginationItem, PaginationLink, Button } from 'reactstrap';
import { withRouter } from 'react-router-dom';
import ModalPost from './ModalPost';
const data = [
    {
        post_id: '',
        title: '',
        content: '',
        user: {
            user_name: ''
        },
    },
]
class ListPost extends React.Component {

    constructor(props) {
        super(props);

        let locationSearch = this.props.location.search;
        let searchParam = new URLSearchParams(locationSearch)

        this.state = {
            list_post: data,
            pageSize: 1,
            total: 10,
            currenPage: parseInt(searchParam.get('current_page') || 1),
            visibleModal: false
        }

        this.doGetPostList = this.doGetPostList.bind(this);
        this.renderPagination = this.renderPagination.bind(this);
        this.setCurrentPage = this.setCurrentPage.bind(this);
    }


    componentDidMount() {
        this.doGetPostList(this.state.currenPage);
    }



    async doGetPostList(offset) {
        try {
            this.setState({
                loading: true,
            });

            const result = await posts({
                offset: offset,
                limit: this.state.pageSize,  
            });

            console.log("result", result.data.limit[0]);


            if (result.err !== 0) {
                console.log('loi');
                return;
            }

            this.setState({
                posts: result.data,
                total: 100,
            });
        } catch (e) {
            console.log(e);
        } finally {
            this.setState({
                loading: false,
            });
        }
    };

    setCurrentPage(index) {
        this.props.history.push(`?current_page=${index}`);
        this.doGetPostList(index);
    }


    renderPagination() {
        let pageNumber = [];
        for (let i = 1; i <= Math.ceil(this.state.total / this.state.pageSize); i++) {
            pageNumber.push(i);
        }
        return <Pagination aria-label="Page navigation example">
            <PaginationItem>
                <PaginationLink first />
            </PaginationItem>
            <PaginationItem>
                <PaginationLink previous />
            </PaginationItem>
            {
                pageNumber.map((index) => (<PaginationItem key={index} onClick={() => this.setCurrentPage(index)}>
                    <PaginationLink >
                        {index}
                    </PaginationLink>
                </PaginationItem>))
            }
            <PaginationItem>
                <PaginationLink next />
            </PaginationItem>
            <PaginationItem>
                <PaginationLink last />
            </PaginationItem>
        </Pagination>
    }


    render() {

        return (
            <div className="container">
                <Button color="danger" onClick={() => this.setState({ ...this.state, visibleModal: !this.state.visibleModal })}>Thêm bài viết mới</Button>
                {this.state.list_post.map((item, index) => { return (<PostItem key={index} post={item} />) })}
                {this.renderPagination()}
                <div>
                    <ModalPost onAddPostSuccess={() => this.setCurrentPage(1)} visible={this.state.visibleModal} closeModal={() => this.setState({ ...this.state, visibleModal: !this.state.visibleModal })} />
                </div>
            </div>
        );
    }
}

export default withRouter(ListPost);
