import React from 'react';
import { Card, CardBody, CardTitle, CardSubtitle, CardText, Button, ButtonGroup } from 'reactstrap';
import { ChatDots, Star, Eye, Trash } from 'react-bootstrap-icons';
import AddComment from "../Post/AddComment";

export default class PostItem extends React.Component {

    constructor(props) {
        super(props);
        this.state={
            visibleComment: false
        }
    }

    render() {
        let { post } = this.props;
        return (
            <Card className="mb-3 mt-3">
                <CardBody>
                    <CardTitle tag="h5">{post.title}</CardTitle>
                    <CardSubtitle tag="h6" className="mb-2 text-muted">{post.user.name}</CardSubtitle>
                    <CardText>{post.content}</CardText>
                    <ButtonGroup>
                        <Button color="success">5 <Star /></Button>
                        <Button color="warning" onClick={() => this.setState({ ...this.state, visibleComment: !this.state.visibleComment })}><ChatDots /></Button>
                        <Button color="info"><Eye /></Button>
                        <Button color="danger"><Trash /></Button>
                    </ButtonGroup>
                </CardBody>
            </Card>
        );
    }
}
