import makeHttpRequest from '../../helper';
import {server} from '../../const';
import {Cookies} from 'react-cookie';

const posts = async function(args){
    const cookies = new Cookies ();
    try{
        const {page_number, number_post_on_page}= args;
        const accessToken = cookies.get('accessToken');
        const request ={
            method:'GET',
            url:`${server}/api/listPost`,
            params: {
                page_number,
                number_post_on_page
            },
            data:{
                ...args,
            },
            headers:{
                access_token:accessToken,
            }
        };
        const result = await makeHttpRequest (request);
        return result.data;
    }catch (e){
        return e;
    }
}
export default posts;
