import makeHttpRequest from '../../helper';
import {server} from '../../const';

const login = async function(args){
    try{
        const request = {
            method:'POST',
            url:`${server}/api/auth/login`,
            data:{
                ...args,
            },
        };
        const result= await makeHttpRequest(request);
        return result.data;
    }catch(e){
        return e;
    }
};
export default login;
