import React from 'react';
import './RegisterForm.css';
class RegisterForm extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      fields: {},
      errors: {}
    }
  }

  handleValidation() {
    let fields = this.state.fields;
    let errors = {};
    let formIsValid = true;

    //Name
    if (!fields["name"]) {
      formIsValid = false;
      errors["name"] = "Không được để trống";
    }

    if (typeof fields["name"] !== "undefined") {
      if (!fields["name"].match(/^[a-zA-Z]+$/)) {
        formIsValid = false;
        errors["name"] = "Không được sử dụng ký tự";
      }
    }

    //Email
    if (!fields["email"]) {
      formIsValid = false;
      errors["email"] = "Không được để trống";
    }
    if (typeof fields["email"] !== "undefined") {

      var pattern = new RegExp(/^(("[\w-\s]+")|([\w-]+(?:\.[\w-]+)*)|("[\w-\s]+")([\w-]+(?:\.[\w-]+)*))(@((?:[\w-]+\.)*\w[\w-]{0,66})\.([a-z]{2,6}(?:\.[a-z]{2})?)$)|(@\[?((25[0-5]\.|2[0-4][0-9]\.|1[0-9]{2}\.|[0-9]{1,2}\.))((25[0-5]|2[0-4][0-9]|1[0-9]{2}|[0-9]{1,2})\.){2}(25[0-5]|2[0-4][0-9]|1[0-9]{2}|[0-9]{1,2})\]?$)/i);
      if (!pattern.test(fields["email"])) {
        formIsValid = false;
        errors["email"] = "Email không hợp lệ";
      }
    }
    //Pass
    if (!fields["password"]) {
      formIsValid = false;
      errors["password"] = "Không được để trống";
    }
    if (!fields["confirmpassword"]) {
      formIsValid = false;
      errors["confirmpassword"] = "Không được để trống";
    }
    if (typeof fields["password"] !== "undefined" && typeof fields["confirmpassword"] !== "undefined") {
      if (fields["password"] != fields["confirmpassword"]) {
        formIsValid = false;
        errors["confirmpasswor"] = "Passwords don't match.";
      }
    }
    //Address
    if (!fields["address"]) {
      formIsValid = false;
      errors["address"] = "Không được để trống";
    }
    //PhoneNumber
    if (!fields["phonenumber"]) {
      formIsValid = false;
      errors["phonenumber"] = "Không được để trống";
    }
    if (typeof fields["phonenumber"] !== "undefined") {
      var pattern = new RegExp(/^[0-9\b]+$/);
      if (!pattern.test(fields["phonenumber"])) {
        formIsValid = false;
        errors["phonenumber"] = "Vui lòng chỉ nhập số";
      } else if (fields["phonenumber"].length != 10) {
        formIsValid = false;
        errors["phonenumber"] = "Số điện thoại không hợp lệ";
      }
    }

    this.setState({ errors: errors });
    return formIsValid;
  }

  contactSubmit(e) {
    e.preventDefault();

    if (this.handleValidation()) {
      alert("Đăng ký thành công");
    } else {
      alert("Đăng ký thất bại")
    }

  }

  handleChange(field, e) {
    let fields = this.state.fields;
    fields[field] = e.target.value;
    this.setState({ fields });
  }
  render() {
    return (
      <main>
        <div className="container">
          <div className="register-form">
            <form onSubmit={this.contactSubmit.bind(this)}>
              <h1>Đăng ký tài khoản mới</h1>
              <div className="input-box">
                <label for="">Name</label>
                <input ref="name" name="name" type="text" placeholder="Nhập tên người dùng"
                  onChange={this.handleChange.bind(this, "name")} value={this.state.fields["name"]} />
                <span style={{ color: "red" }}>{this.state.errors["name"]}</span>
              </div>
              <div className="input-box">
                <label for="">Email</label>
                <p>
                  <input refs="email" name="email" type="text" placeholder="Nhập Email"
                    onChange={this.handleChange.bind(this, "email")} value={this.state.fields["email"]} />
                  <span style={{ color: "red" }}>{this.state.errors["email"]}</span>
                </p>
              </div>
              <div className="input-box">
                <label for="">Password</label>
                <input refs="password" name="password" type="password" placeholder="Nhập mật khẩu"
                  onChange={this.handleChange.bind(this, "password")} value={this.state.fields["password"]} />
                <span style={{ color: "red" }}>{this.state.errors["password"]}</span>
              </div>
              <div className="input-box">
                <label for="">Comfirm Password</label>
                <input name="comfirmpassword" type="password" placeholder="Nhập lai mật khẩu"
                  onChange={this.handleChange.bind(this, "comfirmpassword")} value={this.state.fields["comfirmpassword"]} />
                <span style={{ color: "red" }}>{this.state.errors["comfirmpassword"]}</span>
              </div>
              <div className="input-box">
                <label for="">Address</label>
                <input name="address" type="text" placeholder="Nhập địa chỉ"
                  onChange={this.handleChange.bind(this, "address")} value={this.state.fields["address"]} />
                <span style={{ color: "red" }}>{this.state.errors["address"]}</span>
              </div>
              <div className="input-box">
                <label for="">PhoneNumber</label>
                <input refs="phonenumber" name="phonenumber" type="text" placeholder="Nhập số điện thoại "
                  onChange={this.handleChange.bind(this, "phonenumber")} value={this.state.fields["phonenumber"]} />
                <span style={{ color: "red" }}>{this.state.errors["phonenumber"]}</span>
              </div>
              <div className="input-box">
                <label for="">Gender</label>
                <p>
                  <input name="gender" type="radio" checked />Male
                    <input name="gender" type="radio" />Female
                    </p>
              </div>
              <div className="btn-box">
                <button type="submit" value="Submit">
                  Đăng ký
                   </button>
              </div>
            </form>
          </div>
        </div>
      </main>
    )
  }
}

export default RegisterForm;
