import React from "react";
import {Input,Form,Button,Comment,Avatar} from "antd";
import addComment from '../../services/comment/addComment';
import { Modal} from "reactstrap";
const {TextArea} = Input ;
const Editor =({
    onChange,
    onSubmit,
    submitting,
    value,
})=>(
      <Modal isOpen={this.props.visible || false}>
        <Form.Item>
            <TextArea row={4} onChange={onChange} value={value}/>
        </Form.Item>
        <Form.Item>
            <Button htmlType="submit" loading={submitting} onClick={onSubmit} type="primary">
                Add Comment
            </Button>
        </Form.Item>
        </Modal>

);
class AddComment extends React.Component {
    constructor(props){
        super(props);
        this.state ={
            comment:[],
            submitting:false,
            value:'',
        };
    }
    handleSubmit = (e )=>{
        console.log('comment');
        if(!this.state.value){
            return;
        }
        addComment({
            post:this.props.post.post_id,
            content:this.state.value,
        }).then(res => {
            this.props.post.commentSuccess(res);
          });

          this.setState({
            submitting: true,
          });
        };
        handleChange = (e ) => {
          this.setState({
            value: e.target.value,
          });
          setTimeout(() => {
            this.setState({
              submitting: false,
              value: '',
              comment: [
                {
                  comment_id: this.props.comment_id,
                  content: this.state.value,
                  post_id:this.props.post_id,
                  user:{
                    name:this.props.name,
                    _id:this.props.id,
                  },
                  create_At: this.props.create_At,
                  update_At:this.props.update_At,
                },
                ...this.state.comment,
              ],
            });
          }, 1000);
        };
        render() {
          const { submitting, value } = this.state;

          return (
            <div>
                <Comment 
                  name ={<Avatar alt={this.props.name} />}
                  content={
                    <Editor onChange={this.handleChange} onSubmit={this.handleSubmit} submitting={submitting} value={value} />
                  }
             />
            </div>
          );
        }
      }
      export default AddComment;
