import React from 'react';
import './LoginForm.css';

class LoginForm extends React.Component {
  constructor(props){
    super(props);
    this.state={value:""};
  };
  handleChange=(event)=>{
    this.setState({value:event.target.value});
  };
  handleSumit=(event)=>{
    alert('Đăng nhập thành công:'+this.state.value);
    event.preventDefault();
  }
    render() {
        return (
            <main>
            <div className="container">
              <div className="login-form">
                <form onSubmit={this.handleSumit}>
                  <h1>Đăng Nhập Người Dùng</h1>
                  <div className="input-box">
                    <label for="">Email</label>
                    <p>
                    <input name="email" type="email" placeholder="Nhập Email" value={this.state.value} onChange={this.handleChange} />
                    </p>
                  </div>
                  <div className="input-box">
                    <label for="">Password</label>
                    <input name="password" type="password" placeholder="Nhập mật khẩu" />
                  </div>
                  <div className="btn-box">
                    <button type="submit"value="Sumit">
                      Đăng nhập
                   </button>
                  </div>
                </form>
              </div>
            </div>
          </main>
        )
    }
}

export default LoginForm;
