import React from 'react';
import { Card, CardBody, CardTitle, CardSubtitle, CardText, Button, ButtonGroup } from 'reactstrap';
import { ChatDots, Star, Eye, Trash } from 'react-bootstrap-icons';
import { notification, Modal } from 'antd';
import AddComment from "../Post/AddComment";
import removePost from "../../services/post/removePost";
import { Cookies } from 'react-cookie';
const cookies = new Cookies();
const { confirm } = Modal;

export default class PostItem extends React.Component {

  constructor(props) {
    super(props);
    this.state = {
      visibleComment: false
    }
  }
  componentDidMount() {
    const isLoggedIn = cookies.get(`userInfo`);
  }
  handleDeleteConfirm = () => {
    const doRemovePost = async () => {
      try {
        this.setState({
          loading: true,
        });

        const result = await removePost({
          post_id: this.props.post.post_id,
        });
        if(result.err===0){
          this.props.onAddPostSuccess(true);
          this.props.closeModal();
          return;
        }

        console.log(`result`, result);

        if (result.err !== 0) {
          notification.error({
            message: 'Something went wrong!',
            description: `${result.msg}`,
          });
          return;
        }

        this.props.history.push('/list_post');
      } catch (e) {
        notification.error({
          message: `Something went wrong!`,
          description: `${e.toString()}`,
        });
      } finally {
        this.setState({
          loading: false,
        });
      }
    };

    confirm({
      title: 'Are you sure delete this post?',
      okText: 'Yes',
      okType: 'danger',
      cancelText: 'No',
      onOk() {
        console.log('OK');
        doRemovePost();
      },
      onCancel() {
        console.log('Cancel');
      },
    });
  };

  render() {
    let { post } = this.props;
    const isLoggedIn = !!cookies.get('userInfo');
    const user_info = cookies.get('userInfo');
    return (
      <Card className="mb-3 mt-3">
        <CardBody>
          <CardTitle tag="h5">{post.title}</CardTitle>
          <CardSubtitle tag="h6" className="mb-2 text-muted">{post.user.name}</CardSubtitle>
          <CardText>{post.content}</CardText>
          <ButtonGroup>
            <Button color="success">5 <Star /></Button>
            <Button color="warning" onClick={() => this.setState({ ...this.state, visibleComment: !this.state.visibleComment })}><ChatDots /></Button>
            <Button color="info"><Eye /></Button>
            <Button color="danger"><Trash
              key={1}
              type="delete"
              onClick={this.handleDeleteConfirm}
            // style={{
            //   visibility: isLoggedIn && user_info.post_id === (post && post.post_id) ? 'visible' : 'hidden',
            //  }} 
            />
            </Button>
          </ButtonGroup>
        </CardBody>
      </Card>
    );
  }
}
